__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Turn on Laundry Light when Patio turned on in evening",
    "parent": 0,
    "comment": "COC on Patio during evening hours",
    "if": [
      { "==": { "device":"2F 95 79 1", "control":"DON" } },
      { "and": 1 },
      { "weekly": { "days":"sun,mon,tue,wed,thu,fri,sat", "from":{ "time":"18:00", "to":{ "time":"23:00" } } } }
    ],
    "then": [
      { "device":"2A 60 99 1", "command":"DON", "parameters": [] }
    ],
    "else": [],
    "reasoning": "When the Master Bedroom patio is turned on by control (COC), and it is evening (18:00-23:00), the exterior laundry door light should be turned on for illumination."
  }
}
__END_NUCORE_ROUTINE__
