__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Evening Watch - Pioneer power press after sunset",
    "parent": 0,
    "comment": "When Pioneer 2 is powered on after sunset, power the amp and Apple TV (send SET_BUTTON PowerOn)",
    "if": [
      { "==": { "device": "n010_d60308906", "control": "DON" } },
      { "and": 1 },
      { "at": { "sunset": 0 } }
    ],
    "then": [
      { "device": "n010_d60308902", "command": "DON" },
      { "device": "n010_d60308905", "command": "SET_BUTTON", "parameters": [ { "name": "n/a", "value": 1, "uom": "25", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Use a COC trigger for Pioneer 2 (user pressed power on). Restrict to after sunset using the sunset schedule token. Then power the OSD Amp using its DON command and explicitly send Apple TV a SET_BUTTON PowerOn (enum value 1, uom 25) to ensure correct input state."
  }
}
__END_NUCORE_ROUTINE__
