__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Sunset: Shade motion turns on family lights and garage",
    "parent": 0,
    "comment": "When shade motion starts at sunset, set LIFX to Warm White and brightness; set garage to 80%",
    "if": [
      { "at": { "sunset": 0 } },
      { "and": 1 },
      { "==": { "device": "n005_shade52597", "status": "ST", "value": 1, "uom": "2", "precision": 0 } }
    ],
    "then": [
      { "device": "n002_familyroom", "command": "SET_COLOR", "parameters": [ { "name": "n/a", "value": 10, "uom": "25", "precision": 0 } ] },
      { "device": "n002_familyroom", "command": "SET_HSBKD", "parameters": [ { "name": "Brightness", "value": 40000, "uom": "56", "precision": 1 } ] },
      { "device": "2B 86 64 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 80, "uom": "51", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Trigger: shade motion COS to True combined with sunset ensures actions occur when presence is detected at dusk. Actions set color (Warm White enum 10) and explicit brightness on the LIFX group using SET_HSBKD Brightness parameter; the garage light receives a DON to 80% using its % uom. Precision and uom values match DEVICE STRUCTURE."
  }
}
__END_NUCORE_ROUTINE__
