__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Fast On toggle lamp east ON 75% (evening)",
    "parent": 0,
    "comment": "If switch DFON during evening and lamp east is OFF, set it to 75%",
    "if": [
      { "==": { "device": "17 EA 6A 1", "control": "DFON" } },
      { "and": 1 },
      { "from": { "time": "18:00", "to": { "time": "23:00", "day": 0 } } },
      { "and": 1 },
      { "==": { "device": "46 F1 A2 1", "status": "ST", "value": 0, "uom": "51", "precision": 0 } }
    ],
    "then": [
      {
        "device": "46 F1 A2 1",
        "command": "DON",
        "parameters": [
          { "name": "n/a", "value": 75, "uom": "51", "precision": 0 }
        ]
      }
    ],
    "else": [],
    "reasoning": "Separate routines handle the two outcomes: this routine triggers on the DFON event during the specified time and only when the lamp east is currently off, setting it to 75% using the DON command with a percent parameter (uom 51)."
  }
}
__END_NUCORE_ROUTINE__

__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 2,
    "name": "Fast On toggle lamp east OFF (evening)",
    "parent": 0,
    "comment": "If switch DFON during evening and lamp east is ON, turn it off",
    "if": [
      { "==": { "device": "17 EA 6A 1", "control": "DFON" } },
      { "and": 1 },
      { "from": { "time": "18:00", "to": { "time": "23:00", "day": 0 } } },
      { "and": 1 },
      { ">": { "device": "46 F1 A2 1", "status": "ST", "value": 0, "uom": "51", "precision": 0 } }
    ],
    "then": [
      {
        "device": "46 F1 A2 1",
        "command": "DOF",
        "parameters": []
      }
    ],
    "else": [],
    "reasoning": "This routine handles the case where the lamp east is currently on (ST > 0) when a DFON event occurs during evening hours; it sends DOF to turn the lamp off. Splitting into two routines avoids nested action-condition logic.">
  }
}
__END_NUCORE_ROUTINE__
