__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Weekday fast-on: Sync craft closets",
    "parent": 0,
    "comment": "When the Craft Room Light receives a fast-on during weekday evenings, enable craft closet and disable office closet.",
    "if": [
      { "(": 1 },
        { "==": { "device": "ZY012_1", "control": "DFON" } },
      { ")": 1 },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "18:00", "to": { "time": "22:00" } } } }
    ],
    "then": [
      { "device": "ZY013_1", "command": "DON", "parameters": [ { "name": "n/a", "value": 100, "uom": "78", "precision": 0 } ] },
      { "device": "ZY011_1", "command": "DOF", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Use a COC (DFON) from the Craft Room Light combined with a weekday evening schedule to limit the automation to user-initiated fast-on events; then turn on the Craft Room Closet (DON with uom 78 = On) and turn off the Office Closet (DOF)."
  }
}
__END_NUCORE_ROUTINE__
