__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night - Door unlocked -> Set heat",
    "parent": 0,
    "comment": "If door is unlocked overnight, force heating mode and set comfortable setpoint",
    "if": [
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } },
      { "and": 1 },
      { "==": { "device": "ZY020_1", "status": "ST", "value": 0, "uom": "11" } }
    ],
    "then": [
      { "device": "14 F7 61 1", "command": "CLIMD", "parameters": [ { "name": "n/a", "value": 1, "uom": "98", "precision": 0 } ] },
      { "device": "14 F7 61 1", "command": "CLISPH", "parameters": [ { "name": "n/a", "value": 65, "uom": "14", "precision": 1 } ] }
    ],
    "else": [],
    "reasoning": "During the defined night period (22:00–06:00 day wrap), a COS of the door lock to Unlocked (ST=0) indicates potential entry risk or activity; switch the Workshop Thermostat to Heat (CLIMD=1) and set a safe comfortable heat setpoint (CLISPH=65°F) to ensure the space remains warm overnight. Uses explicit device properties and commands from DEVICE STRUCTURE."
  }
}
__END_NUCORE_ROUTINE__
