__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "CraftRoom_AllOn_triggers_Office_AllOff_weekdays",
    "parent": 0,
    "comment": "When Craft Room All switch is pressed ON during weekdays send All Off to Office Closet",
    "if": [
      { "(": 1 },
        { "==": { "device": "ZY012_183", "control": "ALLON" } },
      { ")": 1 },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "09:00", "to": { "time": "17:00" } } } }
    ],
    "then": [
      { "device": "ZY011_183", "command": "ALLOFF", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Use a COC (physical press) on the Craft Room Switch All (ZY012_183) limited to weekdays 09:00-17:00 to avoid accidental after-hours triggers; action sends ALLOFF to Office Closet Switch All (ZY011_183)."
  }
}
__END_NUCORE_ROUTINE__
