__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Hot Tub Sunset Warmup if Family Room Off",
    "parent": 0,
    "comment": "Set hot tub lights to 60% with 1.0 minute ramp if family room is off at sunset -10m",
    "if": [
      { "at": { "sunset": -600 } },
      { "and": 1 },
      { "==": { "device": "1F D3 5A 1", "status": "ST", "value": 0, "uom": "51", "precision": 0 } }
    ],
    "then": [
      { "device": "20 94 97 1", "command": "OL", "parameters": [ { "name": "n/a", "value": 60, "uom": "51", "precision": 0 } ] },
      { "device": "20 94 97 1", "command": "RR", "parameters": [ { "name": "n/a", "value": 12, "uom": "25", "precision": 0 } ] },
      { "device": "20 94 97 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 60, "uom": "51", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Trigger at sunset-10min to prepare hot tub lights; require Family Room ST == Off to avoid conflict; set On Level then Ramp Rate then issue On at requested level. Uses only defined device IDs, commands, and uoms."
  }
}
__END_NUCORE_ROUTINE__
