__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night beep when Wet fast-on",
    "parent": 0,
    "comment": "Beep Dry when Wet is fast turned on during night; otherwise write changes to Dry",
    "if": [
      { "==": { "device": "3F 5B F 2", "control": "DFON" } },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "3F 5B F 1", "command": "BEEP", "parameters": [ { "name": "n/a", "value": 1, "uom": "100", "precision": 0 } ] }
    ],
    "else": [
      { "device": "3F 5B F 1", "command": "WDU", "parameters": [] }
    ],
    "reasoning": "COC: Use the Fast On (DFON) control from the Wet device as a human/remote control event. The schedule restricts the audible alert to overnight hours. When triggered outside those hours, persist state changes (WDU) instead of sounding an alert."
  }
}
__END_NUCORE_ROUTINE__
