__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Tunnel remote DFON toggles Xmas on during evening",
    "parent": 0,
    "comment": "DFON from Tunnel remote turns Xmas 1 on between 18:00-23:00, otherwise off",
    "if": [
      { "==": { "device":"44 C5 47 1", "control":"DFON" } },
      { "and": 1 },
      { "from": { "time":"18:00", "to": { "time":"23:00","day":0 } } }
    ],
    "then": [
      { "device":"49 2E BC 1", "command":"DON" }
    ],
    "else": [
      { "device":"49 2E BC 1", "command":"DOF" }
    ],
    "reasoning": "Use a COC (DFON) from the Tunnel remote combined with a time window (18:00-23:00). Xmas 1 accepts DON/DOF so use those commands; otherwise turn it off."
  }
}
__END_NUCORE_ROUTINE__
