__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Turn off heater when laundry pump starts at night",
    "parent": 0,
    "comment": "When pump turns on during overnight window, turn heater off",
    "if": [
      { "==": { "device": "ZY087_1", "status": "ST", "value": 100, "uom": "78", "precision": 0 } },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "ZY086_1", "command": "DOF", "parameters": [] }
    ],
    "else": [],
    "reasoning": "This routine listens for the laundry pump status becoming On (COS). If that event occurs during the overnight period 22:00-06:00, it turns the heater off to avoid simultaneous operation and reduce load." 
  }
}
__END_NUCORE_ROUTINE__
