__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Overnight motion turns on living room all on and query scene",
    "parent": 0,
    "comment": "When motion detected overnight turn lights on and query scene responder",
    "if": [
      { "==": { "device": "ZY189_155", "control": "DON" } },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "ZY003_183", "command": "ALLON", "parameters": [] },
      { "device": "ZY003_259", "command": "QUERY", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Use the motion sensor On event (DON) as a COC/COS trigger and restrict actions to the overnight window (22:00-06:00 day wrap) so that the All On command and a scene query only run at night."
  }
}
__END_NUCORE_ROUTINE__
