__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Sunset: Turn on MBATH if Kitchen remote is off",
    "parent": 0,
    "comment": "Turn MBATH on at sunset only when KTCHN Remote - C is off; otherwise beep FP Mtn Sensr-Low twice.",
    "if": [
      { "at": { "sunset": 0 } },
      { "and": 1 },
      { "==": { "device": "3E BE 72 4", "status": "ST", "value": 0, "uom": "51", "precision": 2 } }
    ],
    "then": [
      { "device": "4B 1B E7 1", "command": "DON", "parameters": [] }
    ],
    "else": [
      { "device": "37 BB B3 3", "command": "BEEP", "parameters": [] },
      { "device": "37 BB B3 3", "command": "BEEP", "parameters": [] }
    ],
    "reasoning": "At sunset, check KTCHN Remote - C status; if it's off (ST=0) we safely turn on MBATH Light; if not off, alert the mountain sensor with two beeps as requested. Using equality on the remote ST and the sunset schedule provides the exact trigger and conditional behavior."
  }
}
__END_NUCORE_ROUTINE__
