__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night: Scene C DFON sets Den and Scene D backlights",
    "parent": 0,
    "comment": "When Scene C receives a fast-on during overnight window, adjust backlights for night mode.",
    "if": [
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } },
      { "and": 1 },
      { "==": { "device": "32 FC D5 5", "control": "DFON" } }
    ],
    "then": [
      { "device": "32 FC D5 4", "command": "BL", "parameters": [ { "name": "n/a", "value": 16, "uom": "25", "precision": 0 } ] },
      { "device": "32 FC D5 6", "command": "BL", "parameters": [ { "name": "n/a", "value": 0, "uom": "25", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Use Scene C DFON (COC) as the user control trigger only during the overnight schedule. Actions use the BL accept command on Den Lights Off and Scene D with UOM 25 and integer precision to set explicit backlight indices; no other device properties or commands are referenced."
  }
}
__END_NUCORE_ROUTINE__
