__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Notify mobile when UD Mobile becomes online on weekday mornings",
    "parent": 0,
    "comment": "Notify and query thermostat",
    "if": [
      { "==": { "device": "n001_udmobile", "status": "ST", "value": 1, "uom": "2", "precision": 0 } },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "06:00", "to": { "time": "09:00" } } } }
    ],
    "then": [
      { "device": "n001_udmobile", "command": "GV10", "parameters": [ { "name": "Group", "value": 0, "uom": "25", "precision": 0 }, { "name": "Sound", "value": 2, "uom": "25", "precision": 0 } ] },
      { "device": "ZY008_254N002", "command": "QUERY", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Trigger when UD Mobile 'Last Status' becomes True and time is within weekday mornings; then send an SMS-style notification (GV10 Sound=2) and issue a QUERY to the Upstairs Thermostat for a status snapshot. This ensures the user is notified and the thermostat is polled for current state."
  }
}
__END_NUCORE_ROUTINE__
