__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Sunset - Table Light to 80%",
    "parent": 0,
    "comment": "Set table light backlight at sunset",
    "if": [
      { "at": { "sunset": 0 } }
    ],
    "then": [
      { "device": "9 4F 19 3", "command": "BL", "parameters": [ { "name": "n/a", "value": 80, "uom": "51", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Trigger at sunset to set ControLinc3 (Table Light) backlight to 80% using its BL accept command. This is a simple time-based automation and uses only device-supported parameters."
  }
}
__END_NUCORE_ROUTINE__

__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 102,
    "name": "Bookcase DFON -> Backyard 100%",
    "parent": 0,
    "comment": "When Bookcase is fast-on, set Backyard backlight to full",
    "if": [
      { "==": { "device": "9 4F 19 4", "control": "DFON" } }
    ],
    "then": [
      { "device": "9 4F 19 5", "command": "BL", "parameters": [ { "name": "n/a", "value": 100, "uom": "51", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Use a COC trigger for ControLinc4 (Bookcase) DFON event; when user fast-ons the bookcase light, set ControLinc5 (Backyard) backlight to 100% via its BL command."
  }
}
__END_NUCORE_ROUTINE__
