__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Weekday Mail Open -> Query Sensors",
    "parent": 0,
    "comment": "When mailbox opens during business hours weekdays, poll motion sensor and mailbox for status",
    "if": [
      { "==": { "device": "4E 3A 2A 1", "status": "ST", "value": 100, "uom": "51", "precision": 0 } },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "08:00", "to": { "time": "18:00" } } } }
    ],
    "then": [
      { "device": "27 90 46 3", "command": "QUERY" },
      { "device": "4E 3A 2A 1", "command": "QUERY" }
    ],
    "else": [],
    "reasoning": "Trigger on COS of Mailbox-Opened ST==On limited to weekdays 08:00-18:00 so we only poll devices during business hours; actions use QUERY which is accepted by both target devices per DEVICE STRUCTURE."
  }
}
__END_NUCORE_ROUTINE__
