__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Weekday: query mailbox when workshop window opened",
    "parent": 0,
    "comment": "Query mailbox and acknowledge opened window during business hours",
    "if": [
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "09:00", "to": { "time": "17:00" } } } },
      { "and": 1 },
      { "==": { "device": "37 3 5E 1", "status": "ST", "value": 100, "uom": "51", "precision": 0 } }
    ],
    "then": [
      { "device": "4E 3A 2A 4", "command": "QUERY" },
      { "device": "37 3 5E 1", "command": "BEEP" }
    ],
    "else": [],
    "reasoning": "During weekday business hours, a COS to the workshop-opened sensor (status = On) triggers an immediate mailbox heartbeat query and an acknowledgement beep to the opened sensor. Uses the weekly time window and the ST property equality to represent the open COS."
  }
}
__END_NUCORE_ROUTINE__
