__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night motion -> set backlight",
    "parent": 0,
    "comment": "Turn on backlight when motion detected at night",
    "if": [
      { "(": 1 },
      { "==": { "device":"31 AB F6 1","status":"ST","value":100,"uom":"51","precision":0 } },
      { "and": 1 },
      { "==": { "device":"31 AB F6 1","status":"ERR","value":0,"uom":"25","precision":0 } },
      { ")": 1 },
      { "and": 1 },
      { "from": { "time":"22:00","to":{ "time":"06:00","day":1 } } }
    ],
    "then": [
      {
        "device":"9 4B 9C 5",
        "command":"BL",
        "parameters":[ { "name":"n/a","value":80,"uom":"51","precision":0 } ]
      }
    ],
    "else": [],
    "reasoning":"Enable backlight to 80% when motion sensor 31 AB F6 1 reports ON and is responding, but only during night hours (22:00-06:00)."
  }
}
__END_NUCORE_ROUTINE__
