__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Turn on Palm Lights 1 when Master Closet Disco turned on at night",
    "parent": 0,
    "comment": "Activate Palm Lights 1 when the Master Closet Disco is turned on during overnight hours.",
    "if": [
      { "==": { "device": "ZY009_252N002", "control": "DON" } },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "ZY027_001_1", "command": "DON", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Users turning on the Master Closet Disco at night likely need immediate light; trigger Palm Lights 1 when the closet device is turned on between 22:00 and 06:00. Uses COC from ZY009_252N002 and a time window to avoid daytime activation."
  }
}
__END_NUCORE_ROUTINE__
