__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night manual on -> auto-off",
    "parent": 0,
    "comment": "Auto-off when manually turned on at night",
    "if": [
      { "(": 1 },
        { "==": { "device": "ZY003_1", "control": "DON" } },
      { ")": 1 },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "ZY003_1", "command": "DOF" }
    ],
    "else": [],
    "reasoning": "If the Pool Power Switch is physically turned on (COC) during night hours (22:00-06:00), immediately turn it off to prevent nighttime equipment noise. No action required during daytime manual on events."
  }
}
__END_NUCORE_ROUTINE__
