__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night: Doorbell press opens garage + lights",
    "parent": 0,
    "comment": "When doorbell sensor is pressed at night, quickly activate garage relay and doorbell relay.",
    "if": [
      { "": 0 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } },
      { "and": 1 },
      { "==": { "device": "39 4A D7 1", "control": "DON" } }
    ],
    "then": [
      { "device": "34 8C 58 2", "command": "DFON" },
      { "device": "39 4A D7 2", "command": "DON" }
    ],
    "else": [],
    "reasoning": "Use a weekly-style night time range (22:00 to 06:00) to define night. Trigger is the doorbell sensor sending DON (a physical press). Actions: DFON on the garage relay for a fast activation, and DON on the doorbell relay to turn on the exterior light. No parameters are required for these commands per DEVICE STRUCTURE."
  }
}
__END_NUCORE_ROUTINE__
