__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Weekday F3 press trigger",
    "parent": 0,
    "comment": "Trigger on physical press of IR Button F3 during business hours on weekdays. Device only emits events (send commands), so this routine uses the send command as a trigger.",
    "if": [
      { "==": { "device": "n009_irbutton_13c", "control": "GV1" } },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "09:00", "to": { "time": "17:00" } } } }
    ],
    "then": [],
    "else": [],
    "reasoning": "User requested an automation triggered by pressing F3. The IR Button F3 only provides send commands (GV1=Pressed), so the routine listens for that COC (== with control GV1) and restricts it to weekdays 09:00–17:00. No target devices are defined in the provided DEVICE STRUCTURE, so no actions are included."
  }
}
__END_NUCORE_ROUTINE__
