__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Tree DFON sets Window 80% with 1min ramp",
    "parent": 0,
    "comment": "Physical ON (DFON) of Office Christmas Tree during the specified date/time sets Window level and ramp; else turn window off.",
    "if": [
      { "==": { "device":"4E 5C 29 1","control":"DFON" } },
      { "and": 1 },
      { "from": { "time":"17:00","date":"2025/12/01","to":{ "time":"23:00","date":"2025/12/01" } } }
    ],
    "then": [
      { "device":"35 40 22 1","command":"OL","parameters":[ { "name":"n/a","value":80,"uom":"51","precision":2 } ] },
      { "device":"35 40 22 1","command":"RR","parameters":[ { "name":"n/a","value":12,"uom":"25","precision":0 } ] }
    ],
    "else": [
      { "device":"35 40 22 1","command":"DOF","parameters":[] }
    ],
    "reasoning": "Use COC (DFON) from Office Christmas Tree and limit by explicit date/time window. When triggered set On Level to 80% (uom 51) and Ramp Rate enum value 12 (1.0 minute). Else send DOF to turn window off."
  }
}
__END_NUCORE_ROUTINE__
