__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Entrance DFON turns on foyer lights evening",
    "parent": 0,
    "comment": "Physical Fast On at entrance during evening should illuminate foyer",
    "if": [
      { "==": { "device": "53 73 C5 1", "control": "DFON" } },
      { "and": 1 },
      { "from": { "time":"18:00","to":{ "time":"23:00","day":0 } } }
    ],
    "then": [
      { "device":"53 5B 3A 1","command":"DON","parameters":[{ "name":"n/a","value":100,"uom":"51","precision":0 }]},
      { "device":"53 5B 26 1","command":"DON","parameters":[{ "name":"n/a","value":100,"uom":"51","precision":0 }]},
      { "device":"53 73 C5 1","command":"BL","parameters":[{ "name":"n/a","value":50,"uom":"51","precision":2 }]}
    ],
    "else": [],
    "reasoning": "Use a COC (DFON) on the entrance as the user action; restrict to evening hours; then send On to both bridges and set the entrance backlight to 50% using the BL parameter."
  }
}
__END_NUCORE_ROUTINE__
