__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 201,
    "name": "Weeknight manual livingroom control",
    "parent": 0,
    "comment": "Triggered by physical press during evening weekdays",
    "if": [
      { "(": 1 },
        { "==": { "device": "1D CE 66 1", "control": "DFON" } },
      { ")": 1 },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "18:00", "to": { "time": "22:00" } } } }
    ],
    "then": [
      { "device": "1D CE 66 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 60, "uom": "51", "precision": 0 } ] },
      { "device": "55 CD C5 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 30, "uom": "51", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "When the living room switch is physically pressed (COC DFON) during weekday evening hours, set living room and kitchen hall to comfortable evening levels. This uses the device IDs and percent UOM defined in the device structure."
  }
}
__END_NUCORE_ROUTINE__

__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 202,
    "name": "Sunset island ext",
    "parent": 0,
    "comment": "Turn island exterior on at sunset",
    "if": [
      { "at": { "sunset": 0 } }
    ],
    "then": [
      { "device": "55 CB 20 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 50, "uom": "51", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "At sunset (daily) enable the island exterior light to 50% using the device's DON command and percent UOM."
  }
}
__END_NUCORE_ROUTINE__
