__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Weekday exercise fast-on sets living TV level and ramp",
    "parent": 0,
    "comment": "Triggered by user fast-on on exercise device during weekday mornings",
    "if": [
      { "==": { "device": "1D C8 44 1", "control": "DFON" } },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "06:00", "to": { "time": "09:00" } } } }
    ],
    "then": [
      { "device": "1D C3 43 1", "command": "OL", "parameters": [ { "name": "n/a", "value": 60, "uom": "51", "precision": 0 } ] },
      { "device": "1D C3 43 1", "command": "RR", "parameters": [ { "name": "n/a", "value": 12, "uom": "25", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Use COC (DFON) on the exercise device as the human control trigger and restrict to weekday 06:00-09:00; then set the living-room TV on level to 60% (OL) and set ramp rate to the 1.0 minute enum value (RR value 12)."
  }
}
__END_NUCORE_ROUTINE__
