__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Pause YouTube when Kasa switched off at night",
    "parent": 0,
    "comment": "Pause playback when the Kasa switch is physically turned off during nighttime hours",
    "if": [
      { "==": { "device": "n002_tplkasactl", "control": "DOF" } },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "n005_ytsvc", "command": "PAUSE", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Use a COC (physical off) from the Kasa controller combined with a nightly schedule to pause the YouTube player only during night hours. The action targets the YouTube Player device directly to issue a PAUSE command."
  }
}
__END_NUCORE_ROUTINE__

__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 2,
    "name": "Play YouTube when Kasa switched on in evening",
    "parent": 0,
    "comment": "Start playback when the Kasa switch is physically turned on in the evening",
    "if": [
      { "==": { "device": "n002_tplkasactl", "control": "DON" } },
      { "and": 1 },
      { "from": { "time": "18:00", "to": { "time": "23:00", "day": 0 } } }
    ],
    "then": [
      { "device": "n005_ytsvc", "command": "PLAY", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Use a COC (physical on) from the Kasa controller within the evening window to trigger PLAY on the YouTube Player. Separate routine prevents ambiguous branching in a single IF block."
  }
}
__END_NUCORE_ROUTINE__
