__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "FloorLamp DFON sets level based on weekday evening",
    "parent": 0,
    "comment": "When DFON occurs, use a lower level on weeknights, otherwise full on.",
    "if": [
      { "(": 1 },
        { "==": { "device": "70 AF 4A 1", "control": "DFON" } },
        { "and": 1 },
        { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "21:00", "to": { "time": "23:00" } } } },
      { ")": 1 }
    ],
    "then": [
      { "device": "70 AF 4A 1", "command": "OL", "parameters": [ { "name": "n/a", "value": 40, "uom": "51", "precision": 2 } ] }
    ],
    "else": [
      { "device": "70 AF 4A 1", "command": "OL", "parameters": [ { "name": "n/a", "value": 100, "uom": "51", "precision": 2 } ] }
    ],
    "reasoning": "Use the DFON change-of-control as the trigger. When it occurs during the weekday evening window (21:00-23:00 Mon-Fri) set the On Level to 40% to avoid bright bursts; otherwise set to full (100%)."
  }
}
__END_NUCORE_ROUTINE__
