__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 201,
    "name": "Weekday morning press turns on and sets backlight",
    "parent": 0,
    "comment": "Enable light and backlight when pressed in weekday mornings",
    "if": [
      { "(": 1 },
        { "==": { "device": "20 6C 3C 1", "control": "DFON" } },
        { "or": 1 },
        { "==": { "device": "20 6C 3C 1", "control": "DON" } },
      { ")": 1 },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "06:00", "to": { "time": "08:00" } } } }
    ],
    "then": [
      { "device": "20 6C 3C 1", "command": "DON" },
      { "device": "20 6C 3C 1", "command": "BL", "parameters": [ { "name": "n/a", "value": 100, "uom": "51", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Triggers on a physical Fast On (DFON) or regular On (DON) during weekday mornings (06:00-08:00). Then explicitly send DON to ensure the light is On and set the Backlight (BL) to 100% using the device's defined parameter and UOM."
  }
}
__END_NUCORE_ROUTINE__
