__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Query devices when mailbox detects mail on weekdays",
    "parent": 0,
    "comment": "",
    "if": [
      { "==": { "device": "37 91 C3 4", "status": "ST", "value": 100, "uom": "51", "precision": 0 } },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "09:00", "to": { "time": "17:00" } } } }
    ],
    "then": [
      { "device": "37 91 C3 4", "command": "QUERY", "parameters": [] },
      { "device": "37 94 33 4", "command": "QUERY", "parameters": [] }
    ],
    "else": [],
    "reasoning": "When the mailbox sensor reports On (mail arrived) during weekday working hours, query the mailbox and the garage-heartbeat sensor to collect current states for logging or downstream automations. Using QUERY avoids guessing beep parameters and uses defined accepted commands."
  }
}
__END_NUCORE_ROUTINE__
