__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Open garage when bedroom fan fast-on on weekdays, else beep",
    "parent": 0,
    "comment": "Respond to fan DFON only during weekday daytime; open garage otherwise beep.",
    "if": [
      { "==": { "device": "47 8D E1 1", "control": "DFON" } },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "06:00", "to": { "time": "21:00" } } } }
    ],
    "then": [
      { "device": "19 8C 61 1", "command": "DON", "parameters": [] }
    ],
    "else": [
      { "device": "19 8C 61 1", "command": "BEEP", "parameters": [] }
    ],
    "reasoning": "Use a COC trigger (fan DFON) combined with a weekday time window. If both are true send DON to the garage; otherwise send BEEP. Parameters omitted because BEEP has an optional enum parameter not defined in the structure."
  }
}
__END_NUCORE_ROUTINE__
