__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Front wall lights ON at sunset or manual weekday ON",
    "parent": 0,
    "comment": "Turn on LR4-FRONT WALL LTS at sunset, or when physically turned on during weekday business hours",
    "if": [
      { "at": { "sunset": 0 } },
      { "or": 1 },
      { "(": 1 },
        { "==": { "device": "ZY003_1", "control": "DON" } },
        { "and": 1 },
        { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "09:00", "to": { "time": "17:00" } } } },
      { ")": 1 }
    ],
    "then": [
      { "device": "ZY003_1", "command": "DON", "parameters": [ { "name": "n/a", "value": 100, "uom": "78", "precision": 0 } ] },
      { "device": "ZY004_1", "command": "DON", "parameters": [ { "name": "n/a", "value": 100, "uom": "78", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Use sunset or a physical ON (COC) during weekday business hours to ensure the front wall lights are on; also turn on Christmas lights as a visual indicator when the switch is manually activated during the day. Two OR branches are combined so either event triggers the same ON actions."
  }
}
__END_NUCORE_ROUTINE__
__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 2,
    "name": "Front wall lights OFF at 23:00",
    "parent": 0,
    "comment": "Scheduled nightly off",
    "if": [
      { "at": { "time": "23:00" } }
    ],
    "then": [
      { "device": "ZY003_1", "command": "DOF", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Ensure LR4-FRONT WALL LTS are turned off at 23:00 every night to enforce quiet hours."
  }
}
__END_NUCORE_ROUTINE__
