__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Query rear deck on manual or at sunset",
    "parent": 0,
    "comment": "Query ZY089_251 when manually turned on or at sunset",
    "if": [
      {"(": 1},
        {"==": {"device": "ZY089_251", "control": "DON"}},
        {"or": 1},
        {"at": {"sunset": 0}},
      {")": 1}
    ],
    "then": [
      { "device": "ZY089_251", "command": "QUERY", "parameters": [] }
    ],
    "else": [],
    "reasoning": "User wants status after a physical ON action or at sunset. Use a COC (control == DON) for the physical ON and a schedule (at sunset) combined with OR; action is the device's accepted command QUERY to report its status."
  }
}
__END_NUCORE_ROUTINE__
