__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Keypad DFON evening -> Basement strip 80%",
    "parent": 0,
    "comment": "When either keypad DFON is pressed in the evening, set basement strip to 80%, otherwise turn it off.",
    "if": [
      { "(": 1 },
        { "==": { "device": "22 73 D2 5", "control": "DFON" } },
        { "or": 1 },
        { "==": { "device": "22 73 D2 6", "control": "DFON" } },
      { ")": 1 },
      { "and": 1 },
      { "weekly": { "days": "sun,mon,tue,wed,thu,fri,sat", "from": { "time": "18:00", "to": { "time": "23:00" } } } }
    ],
    "then": [
      { "device": "22 3B 7F 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 80, "uom": "51", "precision": 0 } ] }
    ],
    "else": [
      { "device": "22 3B 7F 1", "command": "DOF" }
    ],
    "reasoning": "Use a COC (DFON) from either keypad and restrict action to evening hours via a weekly time window. When triggered in-window set the basement strip to 80% (DON with 80). If triggered outside the window, issue DOF to ensure the strip is off."
  }
}
__END_NUCORE_ROUTINE__
