__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Set Kitchen Nook to 60% on bedroom fast-on during weekday evenings",
    "parent": 0,
    "comment": "Triggered by Bedroom Light (Right) fast-on control during weekday evening window",
    "if": [
      { "(": 1 },
        { "==": { "device": "11 4A A1 1", "control": "DFON" } },
        { "or": 0 },
      { ")": 1 },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "17:00", "to": { "time": "23:00" } } } }
    ],
    "then": [
      { "device": "32 FC D5 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 60, "uom": "51", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Use a COC trigger on Bedroom Light (DFON) and restrict execution to weekday 17:00-23:00 via a weekly schedule; then send DON with 60% to the Kitchen Nook Ceiling which accepts a 0-100% DON parameter."
  }
}
__END_NUCORE_ROUTINE__
