__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night: Attic trigger turns on output for 5min",
    "parent": 0,
    "comment": "When Attic Temp. is triggered at night, run Output-001 for 300s",
    "if": [
      { "(": 1 },
        { "==": { "device": "n001_zone_3", "control": "DON" } },
        { "or": 1 },
        { "==": { "device": "n001_zone_3", "status": "GV1", "value": 1, "uom": "2", "precision": 0 } },
      { ")": 1 },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "n001_output_1", "command": "DON_WTIME", "parameters": [ { "name": "n/a", "value": 300, "uom": "58", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Use COC (zone On) OR COS (Triggered Alarm GV1==True) restricted to overnight (22:00→06:00 day wrap). Action uses Output-001's Turn On For (DON_WTIME) parameter in seconds (uom 58) set to 300s as defined in DEVICE STRUCTURE."
  }
}
__END_NUCORE_ROUTINE__
