__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Turret DFON after sunset: Front Porch ON, Eaves OFF",
    "parent": 0,
    "comment": "Triggered by a fast-on (COC) on Turret during the evening window",
    "if": [
      { "==": { "device": "21 B 80 1", "control": "DFON" } },
      { "and": 1 },
      { "from": { "sunset": 0, "to": { "time": "23:00", "day": 0 } } }
    ],
    "then": [
      { "device": "21 F 97 1", "command": "DON", "parameters": [] },
      { "device": "21 D 9D 1", "command": "DOF", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Use a COC (DFON) from the Turret as the immediate user control. Constrain to the sunset-to-23:00 window so the fast-on only affects lights in the evening. Then turn on Front Porch and ensure Eaves is off for the desired scene."
  }
}
__END_NUCORE_ROUTINE__
