__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Under-cabinet auto on at sunset -10m",
    "parent": 0,
    "comment": "Turn under-cabinet to 80% at 10 minutes before sunset",
    "if": [
      { "at": { "sunset": -600 } }
    ],
    "then": [
      {
        "device": "26 91 8B 1",
        "command": "DON",
        "parameters": [
          { "name": "n/a", "value": 80, "uom": "51", "precision": 0 }
        ]
      }
    ],
    "else": [],
    "reasoning": "A simple schedule trigger at sunset minus 600s turns the under-cabinet lights on to a comfortable level (80%). Uses the device's DON command with percent UOM 51 and integer precision. No additional conditions needed."
  }
}
__END_NUCORE_ROUTINE__

__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 102,
    "name": "Night mantel on dims Large Landing",
    "parent": 0,
    "comment": "When mantel outlet becomes ON during night, dim Large Landing to 30%",
    "if": [
      { "==": { "device": "E 97 8D 1", "status": "ST", "value": 100, "uom": "51", "precision": 0 } },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      {
        "device": "1D 52 86 1",
        "command": "DON",
        "parameters": [
          { "name": "n/a", "value": 30, "uom": "51", "precision": 0 }
        ]
      }
    ],
    "else": [],
    "reasoning": "Uses a COS check for the mantel outlet status == 100 (ON) combined with a night schedule window. When both are true, the Large Landing is set to 30% to reduce glare during nighttime."
  }
}
__END_NUCORE_ROUTINE__

__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 103,
    "name": "Weekday fast-off sets under-cabinet ramp and stops fade",
    "parent": 0,
    "comment": "On weekdays 06:00-22:00, a physical Fast Off on Large Landing will set Under Cabinet ramp to fastest and stop fades",
    "if": [
      { "==": { "device": "1D 52 86 1", "control": "DFOF" } },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "06:00", "to": { "time": "22:00" } } } }
    ],
    "then": [
      {
        "device": "26 91 8B 1",
        "command": "RR",
        "parameters": [
          { "name": "n/a", "value": 31, "uom": "25", "precision": 0 }
        ]
      },
      {
        "device": "26 91 8B 1",
        "command": "FDSTOP"
      }
    ],
    "else": [],
    "reasoning": "This uses a COC trigger for the Large Landing's Fast Off control combined with a weekday time window. It sets the Under Cabinet ramp rate to the fastest enum value (0.1s -> enum index 31, UOM 25) and issues a Fade Stop to halt any active fades. Both actions target only the Under Cabinet device, respecting device boundaries."
  }
}
__END_NUCORE_ROUTINE__
