__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Evening motion activates FR Picture Lights",
    "parent": 0,
    "comment": "When the motion sensor goes On after sunset, set ramp rate then turn lights to 60%.",
    "if": [
      { "at": { "sunset": 0 } },
      { "and": 1 },
      { "==": { "device": "31 AB F6 3", "status": "ST", "value": 100, "uom": "51", "precision": 0 } }
    ],
    "then": [
      { "device": "4F 69 F1 1", "command": "RR", "parameters": [ { "name": "n/a", "value": 18, "uom": "25", "precision": 0 } ] },
      { "device": "4F 69 F1 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 60, "uom": "51", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Trigger on COS: motion sensor ST == On combined with sunset schedule. First set FR Picture Lights ramp rate to the 30.0 seconds enum (value 18) then command DON at 60% to provide immediate, smooth illumination after motion."
  }
}
__END_NUCORE_ROUTINE__
