__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Spare2 DFON -> Spare2-2 backlight 100",
    "parent": 0,
    "comment": "Physical fast-on on spare 2 sets child backlight",
    "if": [
      { "==": { "device": "5 35 42 1", "control": "DFON" } }
    ],
    "then": [
      { "device": "5 35 42 2", "command": "BL", "parameters": [ { "name": "n/a", "value": 100, "uom": "51", "precision": 0 } ] },
      { "device": "5 35 42 2", "command": "WDU", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Use a COC trigger: DFON on device 5 35 42 1 (spare 2) indicates a physical user control; respond by setting the child device 5 35 42 2 backlight to full (100%) and commit with Write Changes."
  }
}
__END_NUCORE_ROUTINE__
