__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Nightly charge when Gazebo off and Hall on",
    "parent": 0,
    "comment": "Charge Motorcycle Charger to 80% at 22:00 only when Gazebo is off and Upstairs Hall is on; otherwise beep.",
    "if": [
      { "at": { "time": "22:00" } },
      { "and": 1 },
      { "==": { "device": "24 CC 61 1", "status": "ST", "value": 0, "uom": "51", "precision": 0 } },
      { "and": 1 },
      { "==": { "device": "1F BB BC 1", "status": "ST", "value": 100, "uom": "51", "precision": 0 } }
    ],
    "then": [
      { "device": "3E 4E C3 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 80, "uom": "51", "precision": 0 } ] }
    ],
    "else": [
      { "device": "3E 4E C3 1", "command": "BEEP" }
    ],
    "reasoning": "Schedule triggers at 22:00. Ensure Gazebo is Off (safety) and Upstairs Hall is On (presence). If true, set Motorcycle Charger ON to 80%. If not, issue a BEEP notification on the charger. Uses device IDs and uom exactly as provided."
  }
}
__END_NUCORE_ROUTINE__
