__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Closet fast-on opens weekday evening house warm",
    "parent": 0,
    "comment": "Triggered by physical fast-on of Master Closet Lights; sets All Lights to warm 3000K at BR=200 with 1s transition on weekdays 18:00-23:00.",
    "if": [
      { "==": { "device": "57 2C 7E 1", "control": "DFON" } },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "18:00", "to": { "time": "23:00" } } } }
    ],
    "then": [
      {
        "device": "n001_huegrp0",
        "command": "SET_CTBR",
        "parameters": [
          { "name": "K", "value": 3000, "uom": "26", "precision": 1 },
          { "name": "D", "value": 1000, "uom": "42", "precision": 1 },
          { "name": "BR", "value": 200, "uom": "100", "precision": 1 }
        ]
      }
    ],
    "else": [],
    "reasoning": "A COC event (DFON) on Master Closet Lights indicates a deliberate physical control. Restrict to weekday evenings using the weekly schedule. Use SET_CTBR on the Hue group to set color temperature and brightness with a 1s transition. No else actions required."
  }
}
__END_NUCORE_ROUTINE__
