__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Motion at Sunset -> Light On",
    "parent": 0,
    "comment": "Turn on the light when motion is detected exactly at sunset; otherwise turn it off.",
    "if": [
      { "at": { "sunset": 0 } },
      { "and": 1 },
      { "==": { "device": "n018_eh9834cfaf_mo", "status": "ST", "value": 1, "uom": "25", "precision": 0 } }
    ],
    "then": [
      { "device": "n018_eh9834cfaf_lt", "command": "DON" }
    ],
    "else": [
      { "device": "n018_eh9834cfaf_lt", "command": "DOF" }
    ],
    "reasoning": "Use sunset schedule combined with a COS (motion ST == Detected) so the action only fires when motion occurs at sunset. If that combined condition is false at the check moment, explicitly turn the light off to ensure defined state. Uses only devices and commands present in DEVICE STRUCTURE."
  }
}
__END_NUCORE_ROUTINE__
