__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Overnight: Garage Fast-On → Guest Closet dim to 20% w/ 30s ramp",
    "parent": 0,
    "comment": "When garage light is fast-on during overnight hours, quickly set closet light ramp rate then set on level to 20%",
    "if": [
      { "==": { "device": "53 87 DB 1", "control": "DFON" } },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      {
        "device": "53 F0 87 1",
        "command": "RR",
        "parameters": [ { "name": "n/a", "value": 18, "uom": "25", "precision": 0 } ]
      },
      {
        "device": "53 F0 87 1",
        "command": "OL",
        "parameters": [ { "name": "n/a", "value": 20, "uom": "51", "precision": 0 } ]
      }
    ],
    "else": [],
    "reasoning": "Detect manual fast-on of the Garage Light (COC using DFON) only during overnight window (22:00→06:00). Then set Guest Room Closet Light ramp rate to the 30.0s enum (index 18) to make the change quick, then set On Level to 20% using OL parameter (uom 51). Using OL ensures the brightness is set precisely.">
  }
}
__END_NUCORE_ROUTINE__
