__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Weekday morning: DFON on fan -> lights 80% + fan on",
    "parent": 0,
    "comment": "When the fan is fast-turned on during weekday mornings, set center lights to 80% and ensure fan is On.",
    "if": [
      { "(": 1 },
        { "==": { "device": "51 B5 98 1", "control": "DFON" } },
      { ")": 1 },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "06:00", "to": { "time": "09:00" } } } }
    ],
    "then": [
      {
        "device": "51 75 CA 1",
        "command": "DON",
        "parameters": [ { "name": "n/a", "value": 80, "uom": "51", "precision": 0 } ]
      },
      {
        "device": "51 B5 98 1",
        "command": "DON",
        "parameters": [ { "name": "n/a", "value": 100, "uom": "51", "precision": 0 } ]
      }
    ],
    "else": [],
    "reasoning": "Use COC (DFON) on the WC Fan as the trigger, constrained to weekday 06:00-09:00. Then set the Center Lights to 80% (DON with percentage parameter uom 51) and explicitly send DON to the fan with On enum value 100 to ensure it is on. Parent=0 root routine."
  }
}
__END_NUCORE_ROUTINE__
