__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Motor High or Fast-On -> Light Full with Fast Ramp (weekdays 06-22)",
    "parent": 0,
    "comment": "When motor is fast-on or becomes High during the day, quickly bring light to full",
    "if": [
      { "(": 1 },
        { "==": { "device": "51 CD 91 2", "control": "DFON" } },
        { "or": 1 },
        { "==": { "device": "51 CD 91 2", "status": "ST", "value": 100, "uom": "51", "precision": 0 } },
      { ")": 1 },
      { "and": 1 },
      { "from": { "time": "06:00", "to": { "time": "22:00", "day": 0 } } }
    ],
    "then": [
      {
        "device": "51 CD 91 1",
        "command": "RR",
        "parameters": [
          { "name": "n/a", "value": 31, "uom": "25", "precision": 0 }
        ]
      },
      {
        "device": "51 CD 91 1",
        "command": "OL",
        "parameters": [
          { "name": "n/a", "value": 100, "uom": "51", "precision": 0 }
        ]
      },
      {
        "device": "51 CD 91 1",
        "command": "DON",
        "parameters": [
          { "name": "n/a", "value": 100, "uom": "51", "precision": 0 }
        ]
      }
    ],
    "else": [],
    "reasoning": "Trigger on motor fast-on (COC) or motor status reaching High (COS). Restrict to daytime weekdays window. First set the light ramp rate to the fastest enumerated value (31 = 0.1s), set the on-level to 100%, then issue an On command at 100% to ensure the light turns on immediately. Uses only commands and UOMs defined in the DEVICE STRUCTURE."
  }
}
__END_NUCORE_ROUTINE__
