__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Weekday door-open refresh low-battery",
    "parent": 0,
    "comment": "Query low-battery sensor when Closet-C door opens during weekday business hours",
    "if": [
      { "==": { "device": "55 AE 2C 1", "status": "ST", "value": 100, "uom": "51", "precision": 0 } },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "09:00", "to": { "time": "17:00" } } } }
    ],
    "then": [
      { "device": "55 AE 2C 3", "command": "QUERY" }
    ],
    "else": [],
    "reasoning": "Trigger when Closet-C door status becomes On (door opened) and only during weekdays 09:00-17:00; action queries the Low Battery device to refresh its status so any alerting logic can act on fresh data. Uses device IDs and commands exactly as provided." 
  }
}
__END_NUCORE_ROUTINE__
