__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "When Output-025 manually turned on -> Output-026 on for 300s",
    "parent": 0,
    "comment": "",
    "if": [
      { "==": { "device":"n005_output_25","control":"DON" } }
    ],
    "then": [
      { "device":"n005_output_26","command":"DON_WTIME","parameters":[{ "name":"n/a","value":300,"uom":"58","precision":0 }] }
    ],
    "else": [],
    "reasoning": "Use a COC trigger (manual ON) from Output-025 to immediately turn Output-026 on for a fixed interval using DON_WTIME."
  }
}
__END_NUCORE_ROUTINE__
__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 102,
    "name": "When Output-025 changes to Off -> turn Output-026 off",
    "parent": 0,
    "comment": "",
    "if": [
      { "==": { "device":"n005_output_25","status":"ST","value":0,"uom":"78","precision":0 } }
    ],
    "then": [
      { "device":"n005_output_26","command":"DOF","parameters":[] }
    ],
    "else": [],
    "reasoning": "Monitor COS (status becomes Off) for Output-025; when it transitions to Off, ensure Output-026 is turned Off to keep outputs synchronized."
  }
}
__END_NUCORE_ROUTINE__
