__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Night DFON -> dim all bedside backlights",
    "parent": 0,
    "comment": "When keypad F is physically fast-on (DFON) during the night window, set all three table keypads to a low backlight level.",
    "if": [
      { "==": { "device": "4 EA C0 6", "control": "DFON" } },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "4 EA C0 6", "command": "BL", "parameters": [ { "name": "n/a", "value": 16, "uom": "25", "precision": 0 } ] },
      { "device": "4 EA C0 7", "command": "BL", "parameters": [ { "name": "n/a", "value": 16, "uom": "25", "precision": 0 } ] },
      { "device": "4 EA C0 8", "command": "BL", "parameters": [ { "name": "n/a", "value": 16, "uom": "25", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Use a COC trigger (DFON) from keypad F combined with a nightly time window to dim backlights on all three bedside keypads to a low integer level (16) using the BL command (uom 25)."
  }
}
__END_NUCORE_ROUTINE__
